INSERT INTO `patterns` VALUES (1,NULL,'Шаблон-образец пустой','',13,'','','<p align=\"center\"><b>{{contracts.name}} № {{contracts.number}}</b></p>\r\n    <table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n        <tr>\r\n            <td ><b>г. {{contracts.city.name}} </b></td>\r\n            <td width=174 align=\"right\"><b>\r\n{{contracts.date_from|localizeddate(\'none\', \'none\', \'ru_RU\', null, \"d MMMM Y \") }}г.              \r\n\r\n            </b></td>\r\n        </tr>\r\n    </table>\r\n<p>\r\n\r\n{% for role_alias,contractors_list in contractors %}\r\n    {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                {{contragent.data.full_name}}, {{contragent.data.types_company.how}} \r\n                в дальнейшем «{{contragent._joinData.role_name}}», \r\n	        в лице {{contragent.sign_persons.gd.declension}}<b> \r\n                {{contragent.sign_persons.gd.people.fio_sclon}}</b>, \r\n                действующего на основании\r\n	        {{contragent.data.osnovanie}},\r\n                \r\n\r\n            {% elseif contragent._joinData.model_name == \'peoples\'  %}\r\n                <b>{{contragent.data.lastname}} {{contragent.data.name}} {{contragent.data.otchestvo}}</b>,\r\n                {\'.$alias.\'.gender.imenuemye_declension} в дальнейшем \r\n	        «{{contragent._joinData.role_name}}»\r\n            {% endif %}\r\n\r\n        {% if role_alias == \'contractor\' %} с другой стороны {% else %} с одной стороны, и, {% endif %}\r\n\r\n    {% endfor %}   \r\n{% endfor %}\r\n\r\n, совместно именуемые «Стороны», заключили настоящий договор (далее – Договор) о нижеследующем:\r\n</p>\r\n\r\n	            \r\n\r\n	    	   ','<p style=\"text-align: center;padding-top: 30px;\" class=\"signatureTitle\"><b>АДРЕСА, ПОДПИСИ И РЕКВИЗИТЫ СТОРОН</b></p>\r\n       \r\n<table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n    <tr>\r\n        {% for role_alias,role_name in roles %}\r\n            <td width=\"45%\"> <b> {{role_name}}</b></td>\r\n        {% endfor %}\r\n    </tr>\r\n    <tr>\r\n        {% for role_alias,contractors_list in contractors %}\r\n            {% for key,contragent in contractors_list %}\r\n\r\n                <td valign=\"top\">\r\n                    {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                        <p><b>{{contragent.data.full_name}}</b></p>\r\n                        <p><b>ИНН</b> {{contragent.data.inn}} {% if contragent.data.kpp %} КПП {{contragent.data.kpp}}  {%endif%}</p>\r\n                            {% if contragent.data.address_legal %}<p><b>Юридический адрес:</b>\r\n\r\n                            {% if contragent.data.address_legal.postcode %} {{contragent.data.address_legal.postcode}}, {%endif%}\r\n                            {{contragent.data.address_legal.city_name}}, {{contragent.data.address_legal.street}}, д {{contragent.data.address_legal.house}}\r\n                            {% if contragent.data.address_legal.building %}, стр  {{contragent.data.address_legal.building}} {%endif%}\r\n                            {% if contragent.data.address_legal.structure %}, к {{contragent.data.address_legal.structure}} {%endif%}\r\n                            {% if contragent.data.address_legal.flat %}, кв {{contragent.data.address_legal.flat}} {%endif%}\r\n                            {% if contragent.data.address_legal.office %}, оф {{contragent.data.address_legal.office}} {%endif%}\r\n                            </p>\r\n                            {%endif%}\r\n                        {% if contragent.data.address_actual %}<p><b>Фактический адрес:</b>\r\n                            \r\n                            {% if contragent.data.address_actual.postcode %} {{contragent.data.address_actual.postcode}}, {%endif%}\r\n                            {{contragent.data.address_actual.city_name}}, {{contragent.data.address_actual.street}}, д {{contragent.data.address_actual.house}}\r\n                            {% if contragent.data.address_actual.building %}, стр  {{contragent.data.address_actual.building}} {%endif%}\r\n                            {% if contragent.data.address_actual.structure %}, к {{contragent.data.address_actual.structure}} {%endif%}\r\n                            {% if contragent.data.address_actual.flat %}, кв {{contragent.data.address_actual.flat}} {%endif%}\r\n                            {% if contragent.data.address_actual.office %}, оф {{contragent.data.address_actual.office}} {%endif%}\r\n                            </p>\r\n                        {%endif%}\r\n                        {% if contragent.data.address_post %}<p><b>Почтовый адрес:</b>\r\n                           \r\n                            {% if contragent.data.address_post.postcode %} {{contragent.data.address_post.postcode}}, {%endif%}\r\n                            {{contragent.data.address_post.city_name}}, {{contragent.data.address_post.street}}, д {{contragent.data.address_post.house}}\r\n                            {% if contragent.data.address_post.building %}, стр  {{contragent.data.address_post.building}} {%endif%}\r\n                            {% if contragent.data.address_post.structure %}, к {{contragent.data.address_post.structure}} {%endif%}\r\n                            {% if contragent.data.address_post.flat %}, кв {{contragent.data.address_post.flat}} {%endif%}\r\n                            {% if contragent.data.address_post.office %}, оф {{contragent.data.address_post.office}} {%endif%}\r\n                            </p>\r\n                        {%endif%}\r\n\r\n                        {% if contragent.data.bank_account_default %}\r\n                            <p><b>Р/с</b> {{contragent.data.bank_account_default.RS}} в \r\n                            {{contragent.data.bank_account_default.NAMEP}}, {{contragent.data.bank_account_default.NNP}}\r\n	                    <br><b>К/с</b> {{contragent.data.bank_account_default.KSNP}}, <br><b>БИК</b> {{contragent.data.bank_account_default.NEWNUM}}</p>\r\n                        {% endif %}\r\n\r\n\r\n                    {% endif %}                \r\n                </td>\r\n\r\n            {% endfor %}\r\n        {% endfor %}\r\n    </tr> \r\n</table>\r\n                		\r\n','<table  cellpadding=\"0\" width=\"774\" cellspacing=\"0\"  border=\"0\" align=\"center\" style=\"margin-left: 20px; font-size: 13px; font-family: Arial;\">                   \r\n    <tr>\r\n\r\n    {% for role_alias,contractors_list in contractors %}\r\n        {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' %}\r\n                <td width=\"45%\" colspan=\"2\" style=\"padding: 0; height: 50px;\"><b>{{contragent.sign_persons.gd.name}}</b></td>\r\n            {% endif %}\r\n\r\n        {% endfor %}   \r\n    {% endfor %}\r\n\r\n            \r\n    </tr><tr>\r\n\r\n    {% for role_alias,contractors_list in contractors %}\r\n        {% for key,contragent in contractors_list %}   \r\n            <td width=\"20%\"  style=\"border-bottom: 1px solid black; height: 100px;\"> </td>\r\n            <td width=\"23%\"  style=\"vertical-align:bottom;\"><b> {{contragent.sign_persons.gd.people.last_name}} {{contragent.sign_persons.gd.people.initials}}</b></td>\r\n        {% endfor %}   \r\n    {% endfor %}\r\n    </tr>\n        <tr>\n\n               \n            <td style=\"height: 100px;\"> </td>\n            <td></td>\n           \n               \n            <td> </td>\n            <td></td>\n           \n        </tr>\r\n</table>\n\n{# Печати и подписи #} \n{% if output_format is defined and output_format == \"ImagePdf\"  %}\n    {% for role_alias,contractors_list in contractors %}\n        {% for key,contragent in contractors_list %}   \n            \n            {% if contragent.stamp.path %}\n                <div style=\"position: absolute; top: {{contragent.stamp.position.top}}px; left: {{contragent.stamp.position.left}}px;\">\n                    <img src=\"{{contragent.stamp.path}}\">\n                </div>\n            {% endif %}\n\n            {% if contragent.sign_persons.gd.facsimile.path %}\n                <div style=\"position: absolute; top: {{contragent.sign_persons.gd.facsimile.position.top}}px; left: {{contragent.sign_persons.gd.facsimile.position.left}}px;\">\n                    <img src=\"{{contragent.sign_persons.gd.facsimile.path}}\">\n                </div>    \n            {% endif %}\n    \n        {% endfor %}   \n    {% endfor %}\n{% endif %}',NULL,NULL,'off'),(2,NULL,'Договор купли-продажи','',13,'a:2:{s:5:\"roles\";a:2:{s:6:\"client\";s:20:\"Покупатель\";s:10:\"contractor\";s:16:\"Продавец\";}s:12:\"primary_list\";a:5:{s:8:\"contract\";s:1:\"1\";s:7:\"invoice\";s:1:\"1\";s:3:\"act\";s:1:\"1\";s:2:\"sf\";s:1:\"1\";s:10:\"nakladnaya\";s:1:\"1\";}}','a:52:{i:1;a:6:{s:2:\"id\";i:1;s:4:\"text\";s:34:\"1. ПРЕДМЕТ ДОГОВОРА\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:3:\"lft\";i:1;s:4:\"rght\";i:4;}i:2;a:6:{s:2:\"id\";i:3;s:4:\"text\";s:199:\"Продавец передает Покупателю  Товар. Перечень товара и его стоимость указанны в Спецификации (Приложение №1)\";s:9:\"parent_id\";s:1:\"1\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:3;s:3:\"lft\";i:2;}i:5;a:6:{s:2:\"id\";i:6;s:4:\"text\";s:49:\"ПРАВА И ОБЯЗАННОСТИ СТОРОН\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:20;s:3:\"lft\";i:5;}i:6;a:6:{s:2:\"id\";i:7;s:4:\"text\";s:30:\"Продавец обязан:\";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:13;s:3:\"lft\";i:6;}i:7;a:6:{s:2:\"id\";i:8;s:4:\"text\";s:238:\"Передать Покупателю Товар надлежащего качества и в надлежащей упаковке в порядке и в сроки, предусмотренные настоящим Договором.\";s:9:\"parent_id\";s:1:\"7\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:8;s:3:\"lft\";i:7;}i:9;a:6:{s:2:\"id\";i:9;s:4:\"text\";s:83:\"Передать Товар свободным от прав третьих лиц.\";s:9:\"parent_id\";s:1:\"7\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:10;s:3:\"lft\";i:9;}i:11;a:6:{s:2:\"id\";i:11;s:4:\"text\";s:158:\"Произвести проверку качества Товара в порядке, предусмотренном настоящим Договором.\r\n\";s:9:\"parent_id\";s:1:\"7\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:12;s:3:\"lft\";i:11;}i:14;a:6:{s:2:\"id\";i:12;s:4:\"text\";s:34:\"Покупатель обязан:\";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:19;s:3:\"lft\";i:14;}i:15;a:6:{s:2:\"id\";i:13;s:4:\"text\";s:153:\"Принять Товар от Продавца в порядке и в сроки, предусмотренные настоящим Договором.\";s:9:\"parent_id\";s:2:\"12\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:16;s:3:\"lft\";i:15;}i:17;a:6:{s:2:\"id\";i:14;s:4:\"text\";s:133:\"Оплатить Товар в порядке и в сроки, предусмотренные настоящим Договором.\";s:9:\"parent_id\";s:2:\"12\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:18;s:3:\"lft\";i:17;}i:21;a:6:{s:2:\"id\";i:15;s:4:\"text\";s:60:\"ЦЕНА ДОГОВОРА И ПОРЯДОК РАСЧЕТОВ\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:30;s:3:\"lft\";i:21;}i:22;a:6:{s:2:\"id\";i:17;s:4:\"text\";s:84:\"Цена Договора составляет _____ (_______________) рублей.\";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:23;s:3:\"lft\";i:22;}i:24;a:6:{s:2:\"id\";i:18;s:4:\"text\";s:115:\"Цена Договора уплачивается Покупателем в срок до \"__\"___________ ____ г.\";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:25;s:3:\"lft\";i:24;}i:26;a:6:{s:2:\"id\";i:19;s:4:\"text\";s:314:\"Уплата цены Договора производится путем передачи Покупателем наличных денежных средств Продавцу либо путем безналичного перечисления денежных средств на счет Продавца.\";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:27;s:3:\"lft\";i:26;}i:28;a:6:{s:2:\"id\";i:20;s:4:\"text\";s:262:\"Датой оплаты считается дата передачи Покупателем наличных денежных средств Продавцу либо дата поступления денежных средств на счет Продавца.\";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:29;s:3:\"lft\";i:28;}i:31;a:6:{s:2:\"id\";i:21;s:4:\"text\";s:30:\" КАЧЕСТВО ТОВАРА\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:50;s:3:\"lft\";i:31;}i:32;a:6:{s:2:\"id\";i:22;s:4:\"text\";s:195:\"Качество  Товара,  передаваемого  по настоящему  Договору,  должно соответствовать  прилагаемым документам\";s:9:\"parent_id\";s:2:\"21\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:33;s:3:\"lft\";i:32;}i:34;a:6:{s:2:\"id\";i:23;s:4:\"text\";s:194:\"Перед передачей Товара Покупателю Продавец обязан проверить качество Товара заявленного производителем\";s:9:\"parent_id\";s:2:\"21\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:35;s:3:\"lft\";i:34;}i:36;a:6:{s:2:\"id\";i:25;s:4:\"text\";s:311:\"В случае если недостатки Товара не были оговорены Продавцом, Покупатель, которому передан Товар ненадлежащего качества, вправе по своему выбору потребовать от Продавца:\";s:9:\"parent_id\";s:2:\"21\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:43;s:3:\"lft\";i:36;}i:37;a:6:{s:2:\"id\";i:26;s:4:\"text\";s:71:\"Соразмерного уменьшения покупной цены\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:38;s:3:\"lft\";i:37;}i:39;a:6:{s:2:\"id\";i:27;s:4:\"text\";s:116:\" Безвозмездного устранения недостатков Товара в разумный срок.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:40;s:3:\"lft\";i:39;}i:41;a:6:{s:2:\"id\";i:28;s:4:\"text\";s:111:\"Возмещения своих расходов на устранение недостатков Товара.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:42;s:3:\"lft\";i:41;}i:44;a:6:{s:2:\"id\";i:29;s:4:\"text\";s:606:\"В случае существенного нарушения требований к качеству Товара (обнаружения неустранимых недостатков, недостатков, которые не могут быть устранены без несоразмерных расходов или затрат времени или выявляются неоднократно, проявляются вновь после их устранения, и других подобных недостатков) Покупатель вправе по своему выбору:\";s:9:\"parent_id\";s:2:\"21\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:49;s:3:\"lft\";i:44;}i:45;a:6:{s:2:\"id\";i:30;s:4:\"text\";s:172:\"Отказаться от исполнения Договора и потребовать возврата уплаченной за Товар денежной суммы.\";s:9:\"parent_id\";s:2:\"29\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:46;s:3:\"lft\";i:45;}i:47;a:6:{s:2:\"id\";i:31;s:4:\"text\";s:408:\"Направить письменное Требование (претензию) о замене недоброкачественного товара, свойства которого не позволяют устранить его недостатки, товаром надлежащего качества (далее - Требование о замене Товара) (Приложение N ___).\";s:9:\"parent_id\";s:2:\"29\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:48;s:3:\"lft\";i:47;}i:51;a:6:{s:2:\"id\";i:32;s:4:\"text\";s:49:\"ПЕРЕДАЧА И ПРИНЯТИЕ ТОВАРА\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:70;s:3:\"lft\";i:51;}i:52;a:6:{s:2:\"id\";i:33;s:4:\"text\";s:104:\". Товар доставляется Покупателю по адресу: _________________________.\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:53;s:3:\"lft\";i:52;}i:54;a:6:{s:2:\"id\";i:34;s:4:\"text\";s:114:\"Товар должен быть доставлен Покупателю в срок до \"__\"___________ ____ г.\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:55;s:3:\"lft\";i:54;}i:56;a:6:{s:2:\"id\";i:35;s:4:\"text\";s:677:\"Покупатель, которому доставлен Товар, обязан осмотреть его, проверить соответствие качества и количества Товара условиям настоящего Договора и принять Товар.\r\nПроверка качества Товара Покупателем производится в порядке, предусмотренном п. 4.2 настоящего Договора.\r\nПринятие Товара Покупателем подтверждается подписанием акта приема-передачи Товара (Приложение N ___).\r\n\r\n\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:57;s:3:\"lft\";i:56;}i:58;a:6:{s:2:\"id\";i:36;s:4:\"text\";s:303:\"Право собственности на Товар, а также риск случайной гибели или повреждения Товара переходит от Продавца к Покупателю с даты подписания акта приема-передачи Товара.\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:59;s:3:\"lft\";i:58;}i:60;a:6:{s:2:\"id\";i:37;s:4:\"text\";s:230:\"Продавец считается исполнившим свою обязанность по передаче Товара с даты подписания Сторонами акта приема-передачи Товара.\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:61;s:3:\"lft\";i:60;}i:62;a:6:{s:2:\"id\";i:38;s:4:\"text\";s:551:\"Если Продавец передал Покупателю в нарушение п. 1.2 настоящего Договора меньшее количество Товара, Покупатель вправе либо потребовать передать недостающее количество Товара, либо отказаться от переданного Товара и от его оплаты, а если Товар оплачен, потребовать возврата уплаченной денежной суммы.\r\n\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:63;s:3:\"lft\";i:62;}i:64;a:6:{s:2:\"id\";i:39;s:4:\"text\";s:1077:\"Если Продавец не передает или отказывается передать Покупателю относящиеся к Товару принадлежности или документы, указанные в п. п. 2.1.3, 4.3 настоящего Договора, Покупатель вправе назначить ему разумный срок для их передачи.\r\nВ случае когда принадлежности или документы, относящиеся к Товару, не переданы Продавцом в указанный срок, Покупатель вправе отказаться от Товара, направив Продавцу Заявление (претензию) об отказе от товара и возмещении причиненных убытков в связи с непередачей продавцом принадлежностей и документов, относящихся к товару, в установленный срок (Приложение N ___).\r\n\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:65;s:3:\"lft\";i:64;}i:66;a:6:{s:2:\"id\";i:40;s:4:\"text\";s:955:\"Если Продавец передал Покупателю наряду с Товаром, ассортимент которого соответствует Договору (п. 1.2 настоящего Договора), Товар с нарушением условия об ассортименте, Покупатель вправе по своему выбору:\r\n- принять Товар, соответствующий условию об ассортименте, и отказаться от остального Товара;\r\n- отказаться от всего переданного Товара;\r\n- предъявить Требование о замене Товара, не соответствующий условию об ассортименте, на Товар в ассортименте, предусмотренном настоящим Договором;\r\n- принять весь переданный Товар.\r\n\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:67;s:3:\"lft\";i:66;}i:68;a:6:{s:2:\"id\";i:41;s:4:\"text\";s:421:\"При отказе от Товара, ассортимент которого не соответствует условиям Договора, или предъявлении Требования о замене Товара, не соответствующего условию об ассортименте, Покупатель вправе также отказаться от оплаты этого Товара.\";s:9:\"parent_id\";s:2:\"32\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:69;s:3:\"lft\";i:68;}i:71;a:6:{s:2:\"id\";i:42;s:4:\"text\";s:43:\"ОТВЕТСТВЕННОСТЬ СТОРОН\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:78;s:3:\"lft\";i:71;}i:72;a:6:{s:2:\"id\";i:43;s:4:\"text\";s:381:\"За нарушение срока уплаты цены Договора, предусмотренного п. 3.3 настоящего Договора, Продавец вправе потребовать от Покупателя уплаты пени в размере _____% от не уплаченной в срок суммы за каждый день просрочки.\";s:9:\"parent_id\";s:2:\"42\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:73;s:3:\"lft\";i:72;}i:74;a:6:{s:2:\"id\";i:44;s:4:\"text\";s:385:\"За нарушение срока передачи Товара, предусмотренного п. 5.2 настоящего Договора, Покупатель вправе потребовать от Продавца уплаты пени в размере _____% от цены не переданного в срок Товара за каждый день просрочки.\";s:9:\"parent_id\";s:2:\"42\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:75;s:3:\"lft\";i:74;}i:76;a:6:{s:2:\"id\";i:45;s:4:\"text\";s:273:\"За нарушение иных условий настоящего Договора Стороны несут ответственность, предусмотренную действующим законодательством Российской Федерации.\";s:9:\"parent_id\";s:2:\"42\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:77;s:3:\"lft\";i:76;}i:79;a:6:{s:2:\"id\";i:46;s:4:\"text\";s:33:\"РАЗРЕШЕНИЕ СПОРОВ\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:84;s:3:\"lft\";i:79;}i:80;a:6:{s:2:\"id\";i:47;s:4:\"text\";s:440:\"Все споры и разногласия, которые могут возникнуть между Сторонами по вопросам, не нашедшим своего разрешения в тексте настоящего Договора, будут разрешаться путем переговоров на основе действующего законодательства Российской Федерации.\";s:9:\"parent_id\";s:2:\"46\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:81;s:3:\"lft\";i:80;}i:82;a:6:{s:2:\"id\";i:48;s:4:\"text\";s:305:\"При неурегулировании в процессе переговоров спорных вопросов споры разрешаются в суде в порядке, установленном действующим законодательством Российской Федерации.\";s:9:\"parent_id\";s:2:\"46\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:83;s:3:\"lft\";i:82;}i:85;a:6:{s:2:\"id\";i:49;s:4:\"text\";s:48:\"ЗАКЛЮЧИТЕЛЬНЫЕ ПОЛОЖЕНИЯ \";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:104;s:3:\"lft\";i:85;}i:86;a:6:{s:2:\"id\";i:50;s:4:\"text\";s:327:\"Настоящий Договор вступает в силу с даты его подписания уполномоченными представителями обеих Сторон и действует до полного исполнения ими обязательств по настоящему Договору.\";s:9:\"parent_id\";s:2:\"49\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:87;s:3:\"lft\";i:86;}i:88;a:6:{s:2:\"id\";i:51;s:4:\"text\";s:325:\"Любые изменения и дополнения к настоящему Договору должны быть составлены в письменной форме и подписаны Сторонами или надлежаще уполномоченными на то представителями Сторон.\";s:9:\"parent_id\";s:2:\"49\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:89;s:3:\"lft\";i:88;}i:90;a:6:{s:2:\"id\";i:52;s:4:\"text\";s:294:\"Настоящий Договор может быть расторгнут по соглашению Сторон, а также по иным основаниям, предусмотренным действующим законодательством Российской Федерации.\";s:9:\"parent_id\";s:2:\"49\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:91;s:3:\"lft\";i:90;}i:92;a:6:{s:2:\"id\";i:53;s:4:\"text\";s:256:\"Во всем остальном, что не предусмотрено настоящим Договором, Стороны руководствуются действующим законодательством Российской Федерации.\";s:9:\"parent_id\";s:2:\"49\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:93;s:3:\"lft\";i:92;}i:94;a:6:{s:2:\"id\";i:54;s:4:\"text\";s:187:\"Договор составлен в двух экземплярах, имеющих равную юридическую силу, по одному для каждой из Сторон.\";s:9:\"parent_id\";s:2:\"49\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:95;s:3:\"lft\";i:94;}i:96;a:6:{s:2:\"id\";i:55;s:4:\"text\";s:133:\"Неотъемлемой частью настоящего Договора являются следующие приложения:\";s:9:\"parent_id\";s:2:\"49\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:103;s:3:\"lft\";i:96;}i:97;a:6:{s:2:\"id\";i:56;s:4:\"text\";s:79:\"Акт приема-передачи Товара (Приложение N ___).\";s:9:\"parent_id\";s:2:\"55\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:98;s:3:\"lft\";i:97;}i:99;a:6:{s:2:\"id\";i:57;s:4:\"text\";s:303:\"Требование (претензия) о замене недоброкачественного товара, свойства которого не позволяют устранить его недостатки, товаром надлежащего качества (Приложение N ___).\";s:9:\"parent_id\";s:2:\"55\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:100;s:3:\"lft\";i:99;}i:101;a:6:{s:2:\"id\";i:58;s:4:\"text\";s:361:\"Заявление (претензия) об отказе от товара и возмещении причиненных убытков в связи с непередачей продавцом принадлежностей и документов, относящихся к товару, в установленный срок (Приложение N ___).\";s:9:\"parent_id\";s:2:\"55\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:102;s:3:\"lft\";i:101;}}','<p align=\"center\"><b>{{contracts.name}} № {{contracts.number}}</b></p>\r\n    <table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n        <tr>\r\n            <td ><b>г. {{contracts.city.name}} </b></td>\r\n            <td width=174 align=\"right\"><b>\r\n{{contracts.date_from|localizeddate(\'none\', \'none\', \'ru_RU\', null, \"d MMMM Y \") }}г.              \r\n\r\n            </b></td>\r\n        </tr>\r\n    </table>\r\n<p>\r\n\r\n{% for role_alias,contractors_list in contractors %}\r\n    {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                {{contragent.data.full_name}}, {{contragent.data.types_company.how}} \r\n                в дальнейшем «{{contragent._joinData.role_name}}», \r\n	        в лице {{contragent.sign_persons.gd.declension}}<b> \r\n                {{contragent.sign_persons.gd.people.fio_sclon}}</b>, \r\n                действующего на основании\r\n	        {{contragent.data.osnovanie}},\r\n                \r\n\r\n            {% elseif contragent._joinData.model_name == \'peoples\'  %}\r\n                <b>{{contragent.data.lastname}} {{contragent.data.name}} {{contragent.data.otchestvo}}</b>,\r\n                {\'.$alias.\'.gender.imenuemye_declension} в дальнейшем \r\n	        «{{contragent._joinData.role_name}}»\r\n            {% endif %}\r\n\r\n        {% if role_alias == \'contractor\' %} с другой стороны {% else %} с одной стороны, и, {% endif %}\r\n\r\n    {% endfor %}   \r\n{% endfor %}\r\n\r\n, совместно именуемые «Стороны», заключили настоящий договор (далее – Договор) о нижеследующем:\r\n</p>\r\n\r\n	            \r\n\r\n	    	   ','<p style=\"text-align: center;padding-top: 30px;\" class=\"signatureTitle\"><b>АДРЕСА, ПОДПИСИ И РЕКВИЗИТЫ СТОРОН</b></p>\r\n       \r\n<table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n    <tr>\r\n        {% for role_alias,role_name in roles %}\r\n            <td width=\"45%\"> <b> {{role_name}}</b></td>\r\n        {% endfor %}\r\n    </tr>\r\n    <tr>\r\n        {% for role_alias,contractors_list in contractors %}\r\n            {% for key,contragent in contractors_list %}\r\n\r\n                <td valign=\"top\">\r\n                    {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                        <p><b>{{contragent.data.full_name}}</b></p>\r\n                        <p><b>ИНН</b> {{contragent.data.inn}} {% if contragent.data.kpp %} КПП {{contragent.data.kpp}}  {%endif%}</p>\r\n                            {% if contragent.data.address_legal %}<p><b>Юридический адрес:</b>\r\n\r\n                            {% if contragent.data.address_legal.postcode %} {{contragent.data.address_legal.postcode}}, {%endif%}\r\n                            {{contragent.data.address_legal.city_name}}, {{contragent.data.address_legal.street}}, д {{contragent.data.address_legal.house}}\r\n                            {% if contragent.data.address_legal.building %}, стр  {{contragent.data.address_legal.building}} {%endif%}\r\n                            {% if contragent.data.address_legal.structure %}, к {{contragent.data.address_legal.structure}} {%endif%}\r\n                            {% if contragent.data.address_legal.flat %}, кв {{contragent.data.address_legal.flat}} {%endif%}\r\n                            {% if contragent.data.address_legal.office %}, оф {{contragent.data.address_legal.office}} {%endif%}\r\n                            </p>\r\n                            {%endif%}\r\n                        {% if contragent.data.address_actual %}<p><b>Фактический адрес:</b>\r\n                            \r\n                            {% if contragent.data.address_actual.postcode %} {{contragent.data.address_actual.postcode}}, {%endif%}\r\n                            {{contragent.data.address_actual.city_name}}, {{contragent.data.address_actual.street}}, д {{contragent.data.address_actual.house}}\r\n                            {% if contragent.data.address_actual.building %}, стр  {{contragent.data.address_actual.building}} {%endif%}\r\n                            {% if contragent.data.address_actual.structure %}, к {{contragent.data.address_actual.structure}} {%endif%}\r\n                            {% if contragent.data.address_actual.flat %}, кв {{contragent.data.address_actual.flat}} {%endif%}\r\n                            {% if contragent.data.address_actual.office %}, оф {{contragent.data.address_actual.office}} {%endif%}\r\n                            </p>\r\n                        {%endif%}\r\n                        {% if contragent.data.address_post %}<p><b>Почтовый адрес:</b>\r\n                           \r\n                            {% if contragent.data.address_post.postcode %} {{contragent.data.address_post.postcode}}, {%endif%}\r\n                            {{contragent.data.address_post.city_name}}, {{contragent.data.address_post.street}}, д {{contragent.data.address_post.house}}\r\n                            {% if contragent.data.address_post.building %}, стр  {{contragent.data.address_post.building}} {%endif%}\r\n                            {% if contragent.data.address_post.structure %}, к {{contragent.data.address_post.structure}} {%endif%}\r\n                            {% if contragent.data.address_post.flat %}, кв {{contragent.data.address_post.flat}} {%endif%}\r\n                            {% if contragent.data.address_post.office %}, оф {{contragent.data.address_post.office}} {%endif%}\r\n                            </p>\r\n                        {%endif%}\r\n\r\n                        {% if contragent.data.bank_account_default %}\r\n                            <p><b>Р/с</b> {{contragent.data.bank_account_default.RS}} в \r\n                            {{contragent.data.bank_account_default.NAMEP}}, {{contragent.data.bank_account_default.NNP}}\r\n	                    <br><b>К/с</b> {{contragent.data.bank_account_default.KSNP}}, <br><b>БИК</b> {{contragent.data.bank_account_default.NEWNUM}}</p>\r\n                        {% endif %}\r\n\r\n\r\n                    {% endif %}                \r\n                </td>\r\n\r\n            {% endfor %}\r\n        {% endfor %}\r\n    </tr> \r\n</table>\r\n                		\r\n','<table  cellpadding=\"0\" width=\"774\" cellspacing=\"0\"  border=\"0\" align=\"center\" style=\"margin-left: 20px; font-size: 13px; font-family: Arial;\">                   \r\n    <tr>\r\n\r\n    {% for role_alias,contractors_list in contractors %}\r\n        {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' %}\r\n                <td width=\"45%\" colspan=\"2\" style=\"padding: 0; height: 50px;\"><b>{{contragent.sign_persons.gd.name}}</b></td>\r\n            {% endif %}\r\n\r\n        {% endfor %}   \r\n    {% endfor %}\r\n\r\n            \r\n    </tr><tr>\r\n\r\n    {% for role_alias,contractors_list in contractors %}\r\n        {% for key,contragent in contractors_list %}   \r\n            <td width=\"20%\"  style=\"border-bottom: 1px solid black; height: 100px;\"> </td>\r\n            <td width=\"23%\"  style=\"vertical-align:bottom;\"><b> {{contragent.sign_persons.gd.people.last_name}} {{contragent.sign_persons.gd.people.initials}}</b></td>\r\n        {% endfor %}   \r\n    {% endfor %}\r\n    </tr>\n        <tr>\n\n               \n            <td style=\"height: 100px;\"> </td>\n            <td></td>\n           \n               \n            <td> </td>\n            <td></td>\n           \n        </tr>\r\n</table>\n\n{# Печати и подписи #} \n{% if output_format is defined and output_format == \"ImagePdf\"  %}\n    {% for role_alias,contractors_list in contractors %}\n        {% for key,contragent in contractors_list %}   \n            \n            {% if contragent.stamp.path %}\n                <div style=\"position: absolute; top: {{contragent.stamp.position.top}}px; left: {{contragent.stamp.position.left}}px;\">\n                    <img src=\"{{contragent.stamp.path}}\">\n                </div>\n            {% endif %}\n\n            {% if contragent.sign_persons.gd.facsimile.path %}\n                <div style=\"position: absolute; top: {{contragent.sign_persons.gd.facsimile.position.top}}px; left: {{contragent.sign_persons.gd.facsimile.position.left}}px;\">\n                    <img src=\"{{contragent.sign_persons.gd.facsimile.path}}\">\n                </div>    \n            {% endif %}\n    \n        {% endfor %}   \n    {% endfor %}\n{% endif %}',NULL,NULL,'on'),(3,NULL,'Договор поставки','',13,'a:1:{s:5:\"roles\";a:2:{s:6:\"client\";s:20:\"Покупатель\";s:10:\"contractor\";s:16:\"Продавец\";}}','a:29:{i:1;a:6:{s:2:\"id\";i:1;s:4:\"text\";s:31:\"ПРЕДМЕТ ДОГОВОРА\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:3:\"lft\";i:1;s:4:\"rght\";i:6;}i:2;a:6:{s:2:\"id\";i:2;s:4:\"text\";s:453:\"Продавец обязуется поставлять Покупателю запчасти (именуемое далее Продукция) отдельными партиями в течение срока действия настоящего договора  а Покупатель обязуется принимать продукцию и оплачивать в установленные настоящим договором сроки.\";s:9:\"parent_id\";s:1:\"1\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:3;s:3:\"lft\";i:2;}i:4;a:6:{s:2:\"id\";i:3;s:4:\"text\";s:460:\"	Ассортимент, количество и предполагаемая дата поставки Товара согласовываются в Заявках, оформленных Покупателем в соответствии с условиями настоящего договора, и принятыми к исполнению Поставщиком, и фиксируются в товарно-транспортных накладных.\";s:9:\"parent_id\";s:1:\"1\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:5;s:3:\"lft\";i:4;}i:7;a:6:{s:2:\"id\";i:4;s:4:\"text\";s:49:\"ОСНОВНЫЕ УСЛОВИЯ ДОГОВОРА.\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:22;s:3:\"lft\";i:7;}i:8;a:6:{s:2:\"id\";i:5;s:4:\"text\";s:155:\"По настоящему договору Продавец принимает на себя обязательства по поставке Товара.\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:9;s:3:\"lft\";i:8;}i:10;a:6:{s:2:\"id\";i:6;s:4:\"text\";s:144:\"Количество Товара в одной партии определяется на основании заявки Покупателя.\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:11;s:3:\"lft\";i:10;}i:12;a:6:{s:2:\"id\";i:7;s:4:\"text\";s:183:\"Цена на товар указывается в товарно-транспортной накладной, в счет фактуре и включает в себя НДС (18%).\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:13;s:3:\"lft\";i:12;}i:14;a:6:{s:2:\"id\";i:8;s:4:\"text\";s:136:\"В стоимость Товара входит погрузо-разгрузочные работы на складе Продавца.\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:15;s:3:\"lft\";i:14;}i:16;a:6:{s:2:\"id\";i:9;s:4:\"text\";s:254:\"Поставка партии товара производится на основании заказа Покупателя. Покупатель направляет заказ посредством факса или электронной почты\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:17;s:3:\"lft\";i:16;}i:18;a:6:{s:2:\"id\";i:10;s:4:\"text\";s:461:\"Заказ согласовывается Продавцом в течение одного рабочего дня с даты его отправки. В случае не поступления письменного отказа от Продавца от согласования заказа заказ считается полностью согласованным Продавцом и подлежит обязательному исполнению.\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:19;s:3:\"lft\";i:18;}i:20;a:6:{s:2:\"id\";i:11;s:4:\"text\";s:152:\"Сдача - приемка товара оформляется товарно-транспортной накладной и счет фактурой.\";s:9:\"parent_id\";s:1:\"4\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:21;s:3:\"lft\";i:20;}i:23;a:6:{s:2:\"id\";i:12;s:4:\"text\";s:37:\"РАСЧЕТЫ ПО ДОГОВОРУ.\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:26;s:3:\"lft\";i:23;}i:24;a:6:{s:2:\"id\";i:13;s:4:\"text\";s:509:\"Оплата Товара производится путем перечисления денежных средств на расчетный счет Продавца в день передачи\r\nТовара Покупателю. Если поставка совершается в выходной, или праздничный день то оплата совершается в первый рабочий день, следующий за выходными и праздничными днями.\r\n\";s:9:\"parent_id\";s:2:\"12\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:25;s:3:\"lft\";i:24;}i:27;a:6:{s:2:\"id\";i:14;s:4:\"text\";s:50:\"ПРАВА И ОБЯЗАННОСТИ СТОРОН.\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:36;s:3:\"lft\";i:27;}i:28;a:6:{s:2:\"id\";i:15;s:4:\"text\";s:17:\"Продавец:\";s:9:\"parent_id\";s:2:\"14\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:31;s:3:\"lft\";i:28;}i:29;a:6:{s:2:\"id\";i:16;s:4:\"text\";s:243:\"своевременно сообщает о невозможности поставки Товара и принимает все меры для надлежащего исполнения\r\nобязательства по поставке;\r\n\";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:30;s:3:\"lft\";i:29;}i:32;a:6:{s:2:\"id\";i:17;s:4:\"text\";s:21:\"Покупатель:\";s:9:\"parent_id\";s:2:\"14\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:35;s:3:\"lft\";i:32;}i:33;a:6:{s:2:\"id\";i:18;s:4:\"text\";s:139:\"в соответствии с условиями настоящего договора заказывает поставку товара;\";s:9:\"parent_id\";s:2:\"17\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:34;s:3:\"lft\";i:33;}i:37;a:6:{s:2:\"id\";i:19;s:4:\"text\";s:44:\"ОТВЕТСТВЕННОСТЬ СТОРОН.\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:42;s:3:\"lft\";i:37;}i:38;a:6:{s:2:\"id\";i:20;s:4:\"text\";s:733:\"В случае поставки Товара с отсрочкой платежа, при просрочке оплаты Товара Продавец вправе предъявить Покупателю требование об уплате пени в размере 0,1 % от суммы задолженности за каждый день просрочки платежа. В случае нарушения сроков поставки товара Покупатель вправе предъявить Продавцу требование об уплате пении в размере до 0,1 % от суммы не поставленного в срок товара за каждый день просрочки.\";s:9:\"parent_id\";s:2:\"19\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:39;s:3:\"lft\";i:38;}i:40;a:6:{s:2:\"id\";i:21;s:4:\"text\";s:292:\"В случае ненадлежащего исполнения обязательств Продавцом, Покупатель вправе применить санкции,\r\nпредусмотренные действующим гражданским законодательством\r\n\";s:9:\"parent_id\";s:2:\"19\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:41;s:3:\"lft\";i:40;}i:43;a:6:{s:2:\"id\";i:22;s:4:\"text\";s:31:\"СПОРЫ. ФОРС-МАЖОР\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:48;s:3:\"lft\";i:43;}i:44;a:6:{s:2:\"id\";i:23;s:4:\"text\";s:386:\"Споры, возникающие между сторонами при неисполнении или расторжении Договора, подлежат урегулированию\r\nпутем переговоров. При не достижении согласия по спорным вопросам, спор передается на рассмотрение суда.\r\n\";s:9:\"parent_id\";s:2:\"22\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:45;s:3:\"lft\";i:44;}i:46;a:6:{s:2:\"id\";i:24;s:4:\"text\";s:924:\"При наступлении форс-мажорных обстоятельств, невозможности полного или частичного исполнения любой из\r\nсторон, обязательств по настоящему Договору, а именно: стихийные бедствия, вооруженных конфликтов, издание актов органов власти, а также чрезвычайные экономические изменения, которые непосредственно влияют на исполнение обязательств, срок их исполнения отодвигается соразмерно времени, в течении которого действуют такие обстоятельства с оповещением в разумный срок заинтересованной стороны.\r\n\r\n\";s:9:\"parent_id\";s:2:\"22\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:47;s:3:\"lft\";i:46;}i:49;a:6:{s:2:\"id\";i:25;s:4:\"text\";s:41:\"ИНЫЕ УСЛОВИЯ ДОГОВОРА.\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:58;s:3:\"lft\";i:49;}i:50;a:6:{s:2:\"id\";i:26;s:4:\"text\";s:154:\"Настоящий договор составлен в двух подлинных экземплярах, по одному каждой стороне.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:51;s:3:\"lft\";i:50;}i:52;a:6:{s:2:\"id\";i:27;s:4:\"text\";s:617:\"Настоящий договор вступает в силу со дня его подписания и действует по 1  января  018 г. В случае  если по истечении срока действия настоящего договора стороны продолжают выполнять обязательства  предусмотренные настоящим договором  его действие продлевается на следующий календарный год. Далее пролонгация производится в том же порядке.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:53;s:3:\"lft\";i:52;}i:54;a:6:{s:2:\"id\";i:28;s:4:\"text\";s:722:\"Продавец вправе в одностороннем порядке отказаться от выполнения обязательств по настоящему договору в случае отсутствия заказа от Покупателя в течение одного месяца со дня исполнения предыдущего заказа, а также нарушения Покупателем условий настоящего договора. Договор считается расторгнутым с момента направления Покупателю уведомления об одностороннем отказе от исполнения договора.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:55;s:3:\"lft\";i:54;}i:56;a:6:{s:2:\"id\";i:29;s:4:\"text\";s:289:\"В остальном, что не урегулировано настоящим договором, стороны руководствуются соответствующими\r\nположениями действующего гражданского законодательства.\r\n\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:57;s:3:\"lft\";i:56;}}','<p align=\"center\"><b>{{contracts.name}} № {{contracts.number}}</b></p>\r\n    <table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n        <tr>\r\n            <td ><b>г. {{contracts.city.name}} </b></td>\r\n            <td width=174 align=\"right\"><b>\r\n{{contracts.date_from|localizeddate(\'none\', \'none\', \'ru_RU\', null, \"d MMMM Y \") }}г.              \r\n\r\n            </b></td>\r\n        </tr>\r\n    </table>\r\n<p>\r\n\r\n{% for role_alias,contractors_list in contractors %}\r\n    {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                {{contragent.data.full_name}}, {{contragent.data.types_company.how}} \r\n                в дальнейшем «{{contragent._joinData.role_name}}», \r\n	        в лице {{contragent.sign_persons.gd.declension}}<b> \r\n                {{contragent.sign_persons.gd.people.fio_sclon}}</b>, \r\n                действующего на основании\r\n	        {{contragent.data.osnovanie}},\r\n                \r\n\r\n            {% elseif contragent._joinData.model_name == \'peoples\'  %}\r\n                <b>{{contragent.data.lastname}} {{contragent.data.name}} {{contragent.data.otchestvo}}</b>,\r\n                {\'.$alias.\'.gender.imenuemye_declension} в дальнейшем \r\n	        «{{contragent._joinData.role_name}}»\r\n            {% endif %}\r\n\r\n        {% if role_alias == \'contractor\' %} с другой стороны {% else %} с одной стороны, и, {% endif %}\r\n\r\n    {% endfor %}   \r\n{% endfor %}\r\n\r\n, совместно именуемые «Стороны», заключили настоящий договор (далее – Договор) о нижеследующем:\r\n</p>\r\n\r\n	            \r\n\r\n	    	   ','<p style=\"text-align: center;padding-top: 30px;\" class=\"signatureTitle\"><b>АДРЕСА, ПОДПИСИ И РЕКВИЗИТЫ СТОРОН</b></p>\r\n       \r\n<table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n    <tr>\r\n        {% for role_alias,role_name in roles %}\r\n            <td width=\"45%\"> <b> {{role_name}}</b></td>\r\n        {% endfor %}\r\n    </tr>\r\n    <tr>\r\n        {% for role_alias,contractors_list in contractors %}\r\n            {% for key,contragent in contractors_list %}\r\n\r\n                <td valign=\"top\">\r\n                    {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                        <p><b>{{contragent.data.full_name}}</b></p>\r\n                        <p><b>ИНН</b> {{contragent.data.inn}} {% if contragent.data.kpp %} КПП {{contragent.data.kpp}}  {%endif%}</p>\r\n                            {% if contragent.data.address_legal %}<p><b>Юридический адрес:</b>\r\n\r\n                            {% if contragent.data.address_legal.postcode %} {{contragent.data.address_legal.postcode}}, {%endif%}\r\n                            {{contragent.data.address_legal.city_name}}, {{contragent.data.address_legal.street}}, д {{contragent.data.address_legal.house}}\r\n                            {% if contragent.data.address_legal.building %}, стр  {{contragent.data.address_legal.building}} {%endif%}\r\n                            {% if contragent.data.address_legal.structure %}, к {{contragent.data.address_legal.structure}} {%endif%}\r\n                            {% if contragent.data.address_legal.flat %}, кв {{contragent.data.address_legal.flat}} {%endif%}\r\n                            {% if contragent.data.address_legal.office %}, оф {{contragent.data.address_legal.office}} {%endif%}\r\n                            </p>\r\n                            {%endif%}\r\n                        {% if contragent.data.address_actual %}<p><b>Фактический адрес:</b>\r\n                            \r\n                            {% if contragent.data.address_actual.postcode %} {{contragent.data.address_actual.postcode}}, {%endif%}\r\n                            {{contragent.data.address_actual.city_name}}, {{contragent.data.address_actual.street}}, д {{contragent.data.address_actual.house}}\r\n                            {% if contragent.data.address_actual.building %}, стр  {{contragent.data.address_actual.building}} {%endif%}\r\n                            {% if contragent.data.address_actual.structure %}, к {{contragent.data.address_actual.structure}} {%endif%}\r\n                            {% if contragent.data.address_actual.flat %}, кв {{contragent.data.address_actual.flat}} {%endif%}\r\n                            {% if contragent.data.address_actual.office %}, оф {{contragent.data.address_actual.office}} {%endif%}\r\n                            </p>\r\n                        {%endif%}\r\n                        {% if contragent.data.address_post %}<p><b>Почтовый адрес:</b>\r\n                           \r\n                            {% if contragent.data.address_post.postcode %} {{contragent.data.address_post.postcode}}, {%endif%}\r\n                            {{contragent.data.address_post.city_name}}, {{contragent.data.address_post.street}}, д {{contragent.data.address_post.house}}\r\n                            {% if contragent.data.address_post.building %}, стр  {{contragent.data.address_post.building}} {%endif%}\r\n                            {% if contragent.data.address_post.structure %}, к {{contragent.data.address_post.structure}} {%endif%}\r\n                            {% if contragent.data.address_post.flat %}, кв {{contragent.data.address_post.flat}} {%endif%}\r\n                            {% if contragent.data.address_post.office %}, оф {{contragent.data.address_post.office}} {%endif%}\r\n                            </p>\r\n                        {%endif%}\r\n\r\n                        {% if contragent.data.bank_account_default %}\r\n                            <p><b>Р/с</b> {{contragent.data.bank_account_default.RS}} в \r\n                            {{contragent.data.bank_account_default.NAMEP}}, {{contragent.data.bank_account_default.NNP}}\r\n	                    <br><b>К/с</b> {{contragent.data.bank_account_default.KSNP}}, <br><b>БИК</b> {{contragent.data.bank_account_default.NEWNUM}}</p>\r\n                        {% endif %}\r\n\r\n\r\n                    {% endif %}                \r\n                </td>\r\n\r\n            {% endfor %}\r\n        {% endfor %}\r\n    </tr> \r\n</table>\r\n                		\r\n','<table  cellpadding=\"0\" width=\"774\" cellspacing=\"0\"  border=\"0\" align=\"center\" style=\"margin-left: 20px; font-size: 13px; font-family: Arial;\">                   \r\n    <tr>\r\n\r\n    {% for role_alias,contractors_list in contractors %}\r\n        {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' %}\r\n                <td width=\"45%\" colspan=\"2\" style=\"padding: 0; height: 50px;\"><b>{{contragent.sign_persons.gd.name}}</b></td>\r\n            {% endif %}\r\n\r\n        {% endfor %}   \r\n    {% endfor %}\r\n\r\n            \r\n    </tr><tr>\r\n\r\n    {% for role_alias,contractors_list in contractors %}\r\n        {% for key,contragent in contractors_list %}   \r\n            <td width=\"20%\"  style=\"border-bottom: 1px solid black; height: 100px;\"> </td>\r\n            <td width=\"23%\"  style=\"vertical-align:bottom;\"><b> {{contragent.sign_persons.gd.people.last_name}} {{contragent.sign_persons.gd.people.initials}}</b></td>\r\n        {% endfor %}   \r\n    {% endfor %}\r\n    </tr>\n        <tr>\n\n               \n            <td style=\"height: 100px;\"> </td>\n            <td></td>\n           \n               \n            <td> </td>\n            <td></td>\n           \n        </tr>\r\n</table>\n\n{# Печати и подписи #} \n{% if output_format is defined and output_format == \"ImagePdf\"  %}\n    {% for role_alias,contractors_list in contractors %}\n        {% for key,contragent in contractors_list %}   \n            \n            {% if contragent.stamp.path %}\n                <div style=\"position: absolute; top: {{contragent.stamp.position.top}}px; left: {{contragent.stamp.position.left}}px;\">\n                    <img src=\"{{contragent.stamp.path}}\">\n                </div>\n            {% endif %}\n\n            {% if contragent.sign_persons.gd.facsimile.path %}\n                <div style=\"position: absolute; top: {{contragent.sign_persons.gd.facsimile.position.top}}px; left: {{contragent.sign_persons.gd.facsimile.position.left}}px;\">\n                    <img src=\"{{contragent.sign_persons.gd.facsimile.path}}\">\n                </div>    \n            {% endif %}\n    \n        {% endfor %}   \n    {% endfor %}\n{% endif %}',NULL,NULL,'on'),(4,NULL,'Договор предоставления WEB сервиса','',16,'a:2:{s:5:\"sides\";s:1:\"2\";s:5:\"roles\";a:2:{s:10:\"contractor\";s:22:\"Исполнитель\";s:6:\"client\";s:16:\"Заказчик\";}}','a:31:{i:1;a:6:{s:2:\"id\";i:1;s:4:\"text\";s:31:\"ПРЕДМЕТ ДОГОВОРА\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:3:\"lft\";i:1;s:4:\"rght\";i:8;}i:2;a:6:{s:2:\"id\";i:2;s:4:\"text\";s:580:\"Исполнитель  принимает на себя обязательства оказать Заказчику услуги по информационно-технологическому обслуживанию, в дальнейшем именуемые «Услуги», а Заказчик обязуется предоставить Исполнителю сведения, информационные материалы и оплатить Исполнителю оказанные Услуги в порядке, предусмотренном Договором.\";s:9:\"parent_id\";s:1:\"1\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:3;s:3:\"lft\";i:2;}i:4;a:6:{s:2:\"id\";i:3;s:4:\"text\";s:182:\"Базовый перечень Услуг:\r\n- CRM система\r\n- Модуль \"Договора\"\r\n- Модуль \"Справочники\"\r\n- Модуль \"Контрагенты\"\";s:9:\"parent_id\";s:1:\"1\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:5;s:3:\"lft\";i:4;}i:6;a:6:{s:2:\"id\";i:4;s:4:\"text\";s:516:\"Каждая из дополнительных Услуг  не входящая в состав базового набора и оказываемая с момента возникновения потребности до окончания срока действия Договора  выполняется Исполнителем по запросу Заказчика  в течении 1 (одного) календарного месяца  но не более одного запроса в месяц.\";s:9:\"parent_id\";s:1:\"1\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:7;s:3:\"lft\";i:6;}i:9;a:6:{s:2:\"id\";i:5;s:4:\"text\";s:49:\"ПРАВА И ОБЯЗАННОСТИ СТОРОН\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:38;s:3:\"lft\";i:9;}i:10;a:6:{s:2:\"id\";i:6;s:4:\"text\";s:42:\"Исполнитель обязуется:\";s:9:\"parent_id\";s:1:\"5\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:21;s:3:\"lft\";i:10;}i:11;a:6:{s:2:\"id\";i:7;s:4:\"text\";s:392:\"Нести ответственность за выход из строя оборудования (сервера)  вследствие ненадлежащего качества оказания услуг  перерыва в работе и отказы  повлекшие имущественный ущерб Заказчику  при наличии вины Исполнителя.\";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:12;s:3:\"lft\";i:11;}i:13;a:6:{s:2:\"id\";i:8;s:4:\"text\";s:149:\"Обеспечивать работоспособность базового и дополнительного функционала сервиса.\";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:14;s:3:\"lft\";i:13;}i:15;a:6:{s:2:\"id\";i:9;s:4:\"text\";s:464:\"Немедленно информировать Заказчика и до получения от него указаний приостановить оказание Услуг при обнаружении возможных неблагоприятных для Заказчика последствий или иных обстоятельств, препятствующих достижению желаемого Заказчиком результата.\";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:16;s:3:\"lft\";i:15;}i:17;a:6:{s:2:\"id\";i:10;s:4:\"text\";s:782:\"Не позднее 3 (Трех) рабочих дней с момента подписания Договора назначить со своей стороны лицо, ответственное за решение организационных и технических вопросов, связанных с оказанием Услуг по Договору, контролем уровня качества предоставляемых Услуг. Назначенное лицо обязано предоставить Заказчику адреса электронной почты, номера мобильных и рабочих телефонов специалистов Исполнителя, задействованных в оказании Услуг. \";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:18;s:3:\"lft\";i:17;}i:19;a:6:{s:2:\"id\";i:11;s:4:\"text\";s:429:\"Безвозмездно в течение согласованного с Заказчиком срока исправить по требованию Заказчика все выявленные недостатки, если в процессе оказания Услуг Исполнитель допустил отступление от условий Договора, ухудшившее качество Услуг. \";s:9:\"parent_id\";s:1:\"6\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:20;s:3:\"lft\";i:19;}i:22;a:6:{s:2:\"id\";i:12;s:4:\"text\";s:37:\"Заказчик обязуется: \";s:9:\"parent_id\";s:1:\"5\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:27;s:3:\"lft\";i:22;}i:23;a:6:{s:2:\"id\";i:13;s:4:\"text\";s:199:\"Своевременно производить оплату Услуг в соответствии с порядком оплаты, установленным настоящим Договором.\";s:9:\"parent_id\";s:2:\"12\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:24;s:3:\"lft\";i:23;}i:25;a:6:{s:2:\"id\";i:14;s:4:\"text\";s:107:\"Предоставлять информацию, необходимую для оказания Услуг.\";s:9:\"parent_id\";s:2:\"12\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:26;s:3:\"lft\";i:25;}i:28;a:6:{s:2:\"id\";i:15;s:4:\"text\";s:39:\"Заказчик имеет право:\";s:9:\"parent_id\";s:1:\"5\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:33;s:3:\"lft\";i:28;}i:29;a:6:{s:2:\"id\";i:16;s:4:\"text\";s:192:\"Получать консультации по всем вопросам, связанным с использованием предоставляемых Исполнителем Услуг.\";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:30;s:3:\"lft\";i:29;}i:31;a:6:{s:2:\"id\";i:17;s:4:\"text\";s:408:\"В случае ненадлежащего и/или несвоевременного  оказания Услуг направлять в адрес Исполнителя письменно оформленную Претензию, что может явиться основанием снижения оплаты за Услуги на условиях, оговоренных в Соглашении. \";s:9:\"parent_id\";s:2:\"15\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:32;s:3:\"lft\";i:31;}i:34;a:6:{s:2:\"id\";i:18;s:4:\"text\";s:46:\"Исполнитель имеет право: \";s:9:\"parent_id\";s:1:\"5\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:37;s:3:\"lft\";i:34;}i:35;a:6:{s:2:\"id\";i:19;s:4:\"text\";s:383:\"B случае неисполнения Заказчиком п. 2.2. приостановить оказание Услуг. При этом Исполнитель направляет Заказчику соответствующее уведомление с указанием причин приостановки (прекращения) предоставления Услуг\";s:9:\"parent_id\";s:2:\"18\";s:5:\"level\";s:1:\"3\";s:4:\"rght\";i:36;s:3:\"lft\";i:35;}i:39;a:6:{s:2:\"id\";i:20;s:4:\"text\";s:60:\"СТОИМОСТЬ И ПОРЯДОК ОПЛАТЫ УСЛУГ\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:44;s:3:\"lft\";i:39;}i:40;a:6:{s:2:\"id\";i:21;s:4:\"text\";s:415:\"Ежемесячная стоимость определена в приложении N 1. Заказчик перечисляет на расчетный счет Исполнителя денежные средства в размере стоимости оказанных Услуг  авансом за период  не менее 1 (одного) месяца и не более 1 (одного) года.\";s:9:\"parent_id\";s:2:\"20\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:41;s:3:\"lft\";i:40;}i:42;a:6:{s:2:\"id\";i:22;s:4:\"text\";s:246:\"Исполнитель может предоставлять Заказчику бесплатный тестовый период.  В этом случае оплата происходит по окончании данного периода.\";s:9:\"parent_id\";s:2:\"20\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:43;s:3:\"lft\";i:42;}i:45;a:6:{s:2:\"id\";i:23;s:4:\"text\";s:43:\"ОТВЕТСТВЕННОСТЬ СТОРОН\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:48;s:3:\"lft\";i:45;}i:46;a:6:{s:2:\"id\";i:24;s:4:\"text\";s:313:\"Стороны несут ответственность за неисполнение или ненадлежащее исполнение своих обязательств по настоящему Договору в соответствии с действующим законодательством РФ.\";s:9:\"parent_id\";s:2:\"23\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:47;s:3:\"lft\";i:46;}i:49;a:6:{s:2:\"id\";i:25;s:4:\"text\";s:64:\"ОБСТОЯТЕЛЬСТВА НЕПРЕОДОЛИМОЙ СИЛЫ\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:58;s:3:\"lft\";i:49;}i:50;a:6:{s:2:\"id\";i:26;s:4:\"text\";s:1168:\"Стороны не несут ответственности за неисполнение любого из своих обязательств, если докажут, что такое неисполнение было вызвано Форс-мажорными обстоятельствами, т.е. событиями или обстоятельствами, действительно находящимися вне контроля такой Стороны, наступившими после заключения настоящего Договора, носящими непредвиденный и непредотвратимый характер. К форс-мажорным обстоятельствам относятся, в частности, пожары, наводнения, землетрясения, военные действия и т.д. и их последствия, а также – запретительные меры государственных органов, если эти обстоятельства непосредственно повлияли на исполнение настоящего Договора.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:51;s:3:\"lft\";i:50;}i:52;a:6:{s:2:\"id\";i:27;s:4:\"text\";s:381:\"Время, которое требуется Сторонам для исполнения своих обязательств по настоящему Договору, будет продлено на любой срок, в течение которого было отложено исполнение по причине перечисленных обстоятельств.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:53;s:3:\"lft\";i:52;}i:54;a:6:{s:2:\"id\";i:28;s:4:\"text\";s:330:\"В случае продолжительности обстоятельств форс-мажора более 30 (тридцать) дней, любая из Сторон имеет право расторгнуть настоящий Договор по письменному уведомлению другой Стороны.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:55;s:3:\"lft\";i:54;}i:56;a:6:{s:2:\"id\";i:29;s:4:\"text\";s:1119:\"Сторона, для которой стало невозможным исполнение обязательств по настоящему Договору по причине наступления форс-мажорных обстоятельств, должна незамедлительно информировать другую Сторону в письменном виде о возникновении вышеуказанных обстоятельств, а также в течение 30 дней предоставить другой Стороне подтверждение форс-мажорных обстоятельств. Таким подтверждением будет являться справка или иной соответствующий документ, выданный Торгово-промышленной палатой или иной организацией (органом), выполняющей аналогичные функции, расположенными по месту возникновения форс-мажорных обстоятельств.\";s:9:\"parent_id\";s:2:\"25\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:57;s:3:\"lft\";i:56;}i:59;a:6:{s:2:\"id\";i:30;s:4:\"text\";s:42:\"СРОК ДЕЙСТВИЯ ДОГОВОРА\";s:9:\"parent_id\";s:0:\"\";s:5:\"level\";s:1:\"1\";s:4:\"rght\";i:62;s:3:\"lft\";i:59;}i:60;a:6:{s:2:\"id\";i:31;s:4:\"text\";s:128:\"Договор вступает в силу с 02 февраля 2018 года и действует в течении года.\";s:9:\"parent_id\";s:2:\"30\";s:5:\"level\";s:1:\"2\";s:4:\"rght\";i:61;s:3:\"lft\";i:60;}}','<p align=\"center\"><b>{{contracts.name}} № {{contracts.number}}</b></p>\r\n    <table border=\"0\" width=\"100%\" cellspacing=\"10\" cellpadding=\"4\">\r\n        <tr>\r\n            <td ><b>г. {{contracts.city.name}} </b></td>\r\n            <td width=174 align=\"right\"><b>\r\n{{contracts.date_from|localizeddate(\'none\', \'none\', \'ru_RU\', null, \"d MMMM Y \") }}г.              \r\n\r\n            </b></td>\r\n        </tr>\r\n    </table>\r\n<p>\r\n\r\n{% for role_alias,contractors_list in contractors %}\r\n    {% for key,contragent in contractors_list %}\r\n\r\n            {% if contragent._joinData.model_name == \'Companies\' or contragent._joinData.model_name == \'entrepreneurs\' %}\r\n\r\n                {{contragent.data.full_name}}, {{contragent.data.types_company.how}} \r\n                в дальнейшем «{{contragent._joinData.role_name}}», \r\n	        в лице {{contragent.sign_persons.gd.declension}}<b> \r\n                {{contragent.sign_persons.gd.people.fio_sclon}}</b>, \r\n                действующего на основании\r\n	        {{contragent.data.osnovanie}},\r\n                \r\n\r\n            {% elseif contragent._joinData.model_name == \'peoples\'  %}\r\n                <b>{{contragent.data.lastname}} {{contragent.data.name}} {{contragent.data.otchestvo}}</b>,\r\n                {\'.$alias.\'.gender.imenuemye_declension} в дальнейшем \r\n	        «{{contragent._joinData.role_name}}»\r\n            {% endif %}\r\n\r\n        {% if role_alias == \'contractor\' %} с другой стороны {% else %} с одной стороны, и, {% endif %}\r